  area varchar(60) DEFAULT '' COMMENT '区',
  camp_telephone varchar(60) DEFAULT '' COMMENT '电话号码, 默认为负责人电话号码',
  camp_email varchar(60) DEFAULT '' COMMENT '邮箱，默认为负责人邮箱',
