(498, 0, '平台', 0, '平台', '摆脱防守投篮练习', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(499, 0, '平台', 0, '平台', '两人交叉投篮练习', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
