(501, 0, '平台', 0, '平台', '底线追球，一人持球投篮，一人防守', '', 2, '投篮', '教练在底线位置抛出一球，两人一组同时出发追逐球，追上者运球投篮，另一人做防守', '', 1, 1, 1513327501, NULL, 0),
(502, 0, '平台', 0, '平台', '折返跑接球投篮', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(503, 0, '平台', 0, '平台', '干拔投篮', '', 2, '投篮', '指定角度距离快速垂直起跳投篮。\n作用：强化投篮。', '', 1, 1, 1513327501, NULL, 0),
(504, 0, '平台', 0, '平台', '接球急停跳投', '', 2, '投篮', '移动中跨步或跳步接球急停，屈膝降重心，向上举球起跳，当身体接近最高点时，右臂向前上方伸展，手腕前屈，食指中指发力将球拨投出去。', '', 1, 1, 1513327501, NULL, 0),
(505, 0, '平台', 0, '平台', '持球运球急停投篮', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(506, 0, '平台', 0, '平台', '变向运球急停投篮', '', 2, '投篮', '在弧顶的位置做体前变向急停投篮。', '', 1, 1, 1513327501, NULL, 0),
(507, 0, '平台', 0, '平台', '转身跳投', '', 2, '投篮', '转身是利用跨步和身体的转动来改变站立位置和方向的一种动作方法。转身前两膝微屈上体稍向前倾重心在两腿之间转身时移动脚的前掌蹬地跨步同时中枢脚以前掌为轴蹬地上体随移动脚转动向前改变身体方向。', '', 1, 1, 1513327501, NULL, 0),
(508, 0, '平台', 0, '平台', '假动作跳投', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(509, 0, '平台', 0, '平台', '对抗性一攻一守跳投练习', '', 2, '投篮', '在指定的场地，两人一组，有球队员以跳投形式进攻，无球队员尽力做封盖防守。\n作用：提升对位能力。\n', '', 1, 1, 1513327501, NULL, 0),
(510, 0, '平台', 0, '平台', '接球后冲击防护垫运球急停跳投', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(511, 0, '平台', 0, '平台', '一大二小三高跳步伐练习', '', 2, '投篮', '三步上篮脚步练习，第一步跨大一步为了摆脱防守人员，第二步小步为了第三步更好的纵跳高度，最后一步往高跳', '', 1, 1, 1513327501, NULL, 0),
(512, 0, '平台', 0, '平台', '持球原地踩点练习（无球）', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(513, 0, '平台', 0, '平台', '行进间分解动作练习', '', 2, '投篮', '', '', 1, 1, 1513327501, NULL, 0),
(514, 0, '平台', 0, '平台', '持球最后一步屈膝上抬起跳练习', '', 2, '投篮', '此项训练为了练习学员在三步上篮时为杜绝翘脚勾脚等不规范动作。养成良好动作习惯。', '', 1, 1, 1513327501, NULL, 0),
(515, 0, '平台', 0, '平台', '单人对墙传球', '', 3, '传球', '双手持球，进行原地双手对墙传球。\n作用：提高传球的准确性\n', '', 1, 1, 1513327501, NULL, 0),
(516, 0, '平台', 0, '平台', '两人原地传接球', '', 3, '传球', '两人面对面站立，原地进行双手传接球练习。\n作用：提高传球的准确度', '', 1, 1, 1513327501, NULL, 0),
(517, 0, '平台', 0, '平台', '三人或多人同时传球练习', '', 3, '传球', '每人站一边。成三角型。间距3米。用一球原地互相来回传接球。\n作用：提高传接球的准确性', '', 1, 1, 1513327501, NULL, 0),
(518, 0, '平台', 0, '平台', '指定运球数后的同时传球', '', 3, '传球', '两人一组。面对面相距4米。原地运球5下传给队友（运球数可改动）接到球后运球5下再传。\n练习作用：提高运传衔接的能力', '', 1, 1, 1513327501, NULL, 0),
(519, 0, '平台', 0, '平台', '传球后跑位（三人一球）', '', 3, '传球', '3人为一组，3人排一排传球给教练后跑到指定的位置上后再接球。\n练习作用：提高移动接球的能力', '', 1, 1, 1513327501, NULL, 0),
