  `match_lose` int(11) NOT NULL DEFAULT '0' COMMENT '比赛输场次数',
  `event_num` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '球队活动数',
  `fans_num` int(11) NOT NULL DEFAULT '0' COMMENT '粉丝数',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态:1正常(上架)|-1下架',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '最后一次更新时间',
  `delete_time` int(11) DEFAULT NULL COMMENT '软删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='球队表';

-- ----------------------------
-- Records of team
