-- ----------------------------
-- Table structure for `team_comment`
-- ----------------------------
DROP TABLE IF EXISTS `team_comment`;
CREATE TABLE `team_comment` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `comment_type` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '评论类型:1球队|2球队活动|3球队比赛',
  `commented` varchar(100) NOT NULL COMMENT '被评论的实体名称',
  `commented_id` int(10) unsigned NOT NULL COMMENT '被评论的实体id',
  `member_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布评论的会员id',
  `member` varchar(60) NOT NULL COMMENT '发布评论的会员',
  `member_avatar` varchar(200) NOT NULL COMMENT '发布评论的会员头像',
  `comment` varchar(200) DEFAULT NULL COMMENT '文字评论内容',
  `comment_time` int(11) DEFAULT NULL COMMENT '发表文字评论时间',
  `thumbup` tinyint(4) NOT NULL DEFAULT '0' COMMENT '点赞|1点赞|0无点赞',
  `create_time` int(11) NOT NULL,
  `update_time` int(11) NOT NULL,
  `delete_time` int(11) DEFAULT NULL,
