-- ----------------------------
-- Table structure for `team_event`
-- ----------------------------
DROP TABLE IF EXISTS `team_event`;
CREATE TABLE `team_event` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `team_id` int(10) unsigned NOT NULL COMMENT '球队id',
  `team` varchar(50) NOT NULL COMMENT '球队名',
  `member_id` int(11) NOT NULL COMMENT '活动发布会员id',
  `member` varchar(60) NOT NULL COMMENT '活动发布会员',
  `event` varchar(200) NOT NULL COMMENT '活动标题',
  `event_type` int(11) NOT NULL COMMENT '活动类型:内容看model',
  `is_free` int(11) NOT NULL DEFAULT '1' COMMENT '1免费|2收费',
  `price` int(11) NOT NULL DEFAULT '0' COMMENT '活动价格',
  `score` int(11) NOT NULL DEFAULT '0' COMMENT '活动花费积分',
  `finance` int(11) NOT NULL COMMENT '收费结算人',
  `finance_id` varchar(60) NOT NULL COMMENT '收费结算人会员id',
  `start_time` int(11) DEFAULT NULL COMMENT '活动开始时间',
  `end_time` int(11) DEFAULT NULL COMMENT '活动截止时间',
  `event_time` int(11) DEFAULT NULL COMMENT '活动举行时间',
  `reg_number` int(11) NOT NULL DEFAULT '0' COMMENT '报名人数',
  `province` varchar(60) NOT NULL COMMENT '所在地区-省',
  `city` varchar(60) NOT NULL COMMENT '所在地区-市',
  `area` varchar(60) NOT NULL COMMENT '所在地区-区',
  `location` varchar(200) NOT NULL COMMENT '具体地址',
  `max` int(11) NOT NULL DEFAULT '1' COMMENT '最大参与数',
  `min` int(11) NOT NULL DEFAULT '1' COMMENT '最小参与数',
  `event_des` text COMMENT '活动简介',
  `contact` varchar(30) NOT NULL COMMENT '联系人名字',
  `telephone` varchar(20) NOT NULL COMMENT '联系电话',
  `cover` varchar(200) NOT NULL COMMENT '封面图',
  `remarks` varchar(200) NOT NULL COMMENT '备注信息',
  `is_max` int(11) NOT NULL DEFAULT '1' COMMENT '1未满人|-1已满人',
  `is_finished` int(11) NOT NULL DEFAULT '0' COMMENT '0未完成|1已完成',
  `send_message` tinyint(4) NOT NULL DEFAULT '0' COMMENT '发布通知1:通知|0不通知',
  `album` text COMMENT '活动相册(json格式)',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '1正常|-1下架',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `delete_time` int(11) DEFAULT NULL COMMENT '软删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='球队活动表';

-- ----------------------------
-- Records of team_event
-- ----------------------------

-- ----------------------------
-- Table structure for `team_event_member`
-- ----------------------------
DROP TABLE IF EXISTS `team_event_member`;
CREATE TABLE `team_event_member` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `event_id` int(11) NOT NULL COMMENT '活动id',
  `event` varchar(100) NOT NULL COMMENT '活动',
  `member_id` int(11) NOT NULL COMMENT '会员id',
  `member` varchar(60) NOT NULL COMMENT '会员名',
  `student_id` int(11) NOT NULL COMMENT '会员学生id',
  `student` varchar(60) NOT NULL COMMENT '会员学生名',
  `avatar` varchar(200) NOT NULL COMMENT '会员/学生头像',
  `contact_tel` varchar(20) NOT NULL COMMENT '联系人电话',
  `contact` varchar(20) NOT NULL COMMENT '联系人',
  `is_pay` tinyint(4) NOT NULL DEFAULT '1' COMMENT '1已缴费(报名免费活动也是1)|2未缴费',
  `is_sign` tinyint(4) NOT NULL DEFAULT '1' COMMENT '0未签到|1已签到',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '1已报名|2已签到|3已参与|4退出',
  `remarks` varchar(200) NOT NULL COMMENT '备注',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `delete_time` int(11) DEFAULT NULL COMMENT '软删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='球队活动-会员关联';

