INSERT INTO `exercise` VALUES ('465', '0', '平台', '0', '平台', '抢尾巴（无球、运球）', '1', '热身游戏', '游戏规则：每人一条绳子系在腰后当尾巴，游戏开始后，在保护自己尾巴的同时，把别人的尾巴抢走。\n练习作用：提高移动和摆脱能力、敏捷性、反应能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('466', '0', '平台', '0', '平台', '雪糕筒大战（无球、运球）', '1', '热身游戏', '游戏规则：两组队员同时进行，A组负责将场地上的雪糕筒推到，B组负责扶起。\n练习作用：提高团队协作、移动、反应能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('467', '0', '平台', '0', '平台', '红绿灯（无球、运球）', '1', '热身游戏', '游戏规则：一人在终点充当红绿灯背向起点（为红灯）面向终点（为绿灯）。其余队员在起点做为路人，当遇到红灯时立即停止前进，遇到绿灯时迅速前进。\n练习作用：提高反应、速度、团队合作能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('468', '0', '平台', '0', '平台', '贴膏药', '1', '热身游戏', '游戏规则：1人追逐另1人逃脱。其余队员分别两两前后站立，围成一个大圆。逃脱者逃跑过程中可随意贴于前后站立队员的身后喊“贴”，此时换一位该组的成员逃脱，直至追逐者触及逃脱者。\n作用：提高反应及摆脱能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('469', '0', '平台', '0', '平台', '长江黄河', '1', '热身游戏', '游戏规则：全体分两组A组指定为长江，B组指定为黄河。当裁判喊“黄河”则黄河队逃跑，长江队追逐。如此反复进行。\n练习作用：提高反应、速度、协调能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('470', '0', '平台', '0', '平台', '贴报纸跑（无球 、运球）', '1', '热身游戏', '游戏规则：报纸贴于胸前，分两组进行跑动接力比赛，跑动过程中报纸不能掉地。\n练习作用：提高速度和团队合作能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('471', '0', '平台', '0', '平台', '抢呼啦圈（无球 、运球）', '1', '热身游戏', '游戏规则：取与游戏人数少1的呼啦圈个数，分别摆成一个大圆，哨声响起则游戏开始，哨声再次响起全体人员快速抢占一个呼啦圈，未抢到者淘汰出局，并减少一个呼啦圈。\n练习作用：提高反应、策略。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('472', '0', '平台', '0', '平台', '穿越（无球 、运球）', '1', '热身游戏', '游戏规则：由起点运球至终点，在运球过程中必须成功闯过，场地中的拦截者。\n练习作用：提高运球保护能力以及稳定性，增强移动、摆脱能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('473', '0', '平台', '0', '平台', '躲避报纸（橄榄球）', '1', '热身游戏', '游戏规则：全体成员手持报纸，在指定区域内活动，同时运用报纸砸向其余任意队员。\n练习作用：提高敏捷、反应、运球能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('474', '0', '平台', '0', '平台', '抓人游戏（黑魔王&白天使）', '1', '热身游戏', '', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('475', '0', '平台', '0', '平台', '运球 1-2-3', '1', '热身游戏', '游戏规则：全体队员运球往指定方向移动，当裁判喊“1”则全体急停原地高运球，喊“2”则急停后蹲下低运球，喊“3”则急停后进行180°转身反向运球。\n练习作用：提高反应、协调性、运球能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('476', '0', '平台', '0', '平台', '追逐游戏', '1', '热身游戏', '游戏规则：指定一人为追逐者，其余为逃脱者，在指定区域内进行游戏，当逃脱者被追逐着触及时，必须原地站立等待救援。\n练习作用：提高反应能力、运球稳定性、移动能力。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('477', '0', '平台', '0', '平台', '徒手练习投篮动作', '2', '投篮', '无球做原地投篮动作的模仿练习，体会动作方法。\n作用：矫正投篮姿势。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('478', '0', '平台', '0', '平台', '单手对空直投', '2', '投篮', '以右手原地单手肩上投篮为例。由双手持球开始然后将球引至右肩前上方右臂屈肘肘关节稍内收上臂与肩关节约成水平，前臂与上臂大约成90°右手五指自然张开，手腕后屈，掌心空出。将球投出直上直下', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('479', '0', '平台', '0', '平台', '屈膝地上投篮', '2', '投篮', '单膝着地，依靠上肢力量，用正规投篮手型将球投出。\n作用：体会投篮手法和用力过程，注意弧度和旋转。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('480', '0', '平台', '0', '平台', '持球两人对投篮练习', '2', '投篮', '两人一组，面对面站立。用一个篮球做对投篮练习。\n作用：体会投篮手法和用力过程，注意弧度和旋转。矫正投篮手型。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('481', '0', '平台', '0', '平台', '篮框左右自投自抢', '2', '投篮', '在篮筐下左右两边投篮，并保护篮板。如此循环。\n作用：训练判断篮球落点，提升篮下投篮命中率。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('482', '0', '平台', '0', '平台', '定点投篮', '2', '投篮', '在指定角度距离练习正规投篮。\n作用：练习正确投篮手法，体会投篮球出手时的弧度和旋转。', '', '1', '1', '1513327501', null, '0');
