INSERT INTO `exercise` VALUES ('484', '0', '平台', '0', '平台', '无球跑动接球投篮', '2', '投篮', '一人做无球跑位到指定点，接球投篮。\n作用：练习无球跑动意识。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('485', '0', '平台', '0', '平台', '反跑接球投篮', '2', '投篮', '无球员先往一个方向跑,在队友传球的时候你再往相反方向跑动接球投篮。\n作用：练习无球跑动，精准投篮。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('486', '0', '平台', '0', '平台', '挡拆投篮', '2', '投篮', '一人行进间运球在挡拆过后，摆脱防守人员，进行投篮。\n作用:增强投篮能力，锻炼团队意识。', '', '1', '1', '1513327501', null, '0');
