INSERT INTO `exercise` VALUES ('504', '0', '平台', '0', '平台', '接球急停跳投', '2', '投篮', '移动中跨步或跳步接球急停，屈膝降重心，向上举球起跳，当身体接近最高点时，右臂向前上方伸展，手腕前屈，食指中指发力将球拨投出去。', '', '1', '1', '1513327501', null, '0');
