INSERT INTO `exercise` VALUES ('506', '0', '平台', '0', '平台', '变向运球急停投篮', '2', '投篮', '在弧顶的位置做体前变向急停投篮。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('507', '0', '平台', '0', '平台', '转身跳投', '2', '投篮', '转身是利用跨步和身体的转动来改变站立位置和方向的一种动作方法。转身前两膝微屈上体稍向前倾重心在两腿之间转身时移动脚的前掌蹬地跨步同时中枢脚以前掌为轴蹬地上体随移动脚转动向前改变身体方向。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('508', '0', '平台', '0', '平台', '假动作跳投', '2', '投篮', '', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('509', '0', '平台', '0', '平台', '对抗性一攻一守跳投练习', '2', '投篮', '在指定的场地，两人一组，有球队员以跳投形式进攻，无球队员尽力做封盖防守。\n作用：提升对位能力。\n', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('510', '0', '平台', '0', '平台', '接球后冲击防护垫运球急停跳投', '2', '投篮', '', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('511', '0', '平台', '0', '平台', '一大二小三高跳步伐练习', '2', '投篮', '三步上篮脚步练习，第一步跨大一步为了摆脱防守人员，第二步小步为了第三步更好的纵跳高度，最后一步往高跳', '', '1', '1', '1513327501', null, '0');
