INSERT INTO `exercise` VALUES ('514', '0', '平台', '0', '平台', '持球最后一步屈膝上抬起跳练习', '2', '投篮', '此项训练为了练习学员在三步上篮时为杜绝翘脚勾脚等不规范动作。养成良好动作习惯。', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('515', '0', '平台', '0', '平台', '单人对墙传球', '3', '传球', '双手持球，进行原地双手对墙传球。\n作用：提高传球的准确性\n', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('516', '0', '平台', '0', '平台', '两人原地传接球', '3', '传球', '两人面对面站立，原地进行双手传接球练习。\n作用：提高传球的准确度', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('517', '0', '平台', '0', '平台', '三人或多人同时传球练习', '3', '传球', '每人站一边。成三角型。间距3米。用一球原地互相来回传接球。\n作用：提高传接球的准确性', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('518', '0', '平台', '0', '平台', '指定运球数后的同时传球', '3', '传球', '两人一组。面对面相距4米。原地运球5下传给队友（运球数可改动）接到球后运球5下再传。\n练习作用：提高运传衔接的能力', '', '1', '1', '1513327501', null, '0');
INSERT INTO `exercise` VALUES ('519', '0', '平台', '0', '平台', '传球后跑位（三人一球）', '3', '传球', '3人为一组，3人排一排传球给教练后跑到指定的位置上后再接球。\n练习作用：提高移动接球的能力', '', '1', '1', '1513327501', null, '0');
