INSERT INTO `exercise` VALUES ('521', '0', '平台', '0', '平台', '四角传球', '3', '传球', '4人一组。每人站一个角成四角形。用一球顺时针传接球。（可以用两个球或更多。\n练习作用：提高传接球的速度', '', '1', '1', '1513327501', null, '0');
